/**
 * Базовый интерфейс маркера карты
 *
 * @todo подумать про наследование от стандартного маркера OpenLayers
 * @todo SelectableMarker extends BaseMarker
 */
import { wrapCoords } from 'utils/geo';

const IS_MSK = true;

export default class Marker {
  constructor(point, map) {
    this.point = point;

    // some methods may be useful
    this.store = map._pointsStore;

    // todo this.owner
    this._reactMap = map;

    this.map = map.map;
    this.image = null;
    this.radius = 0;
    this.coords = this.getCoords()

    this.animation = null;
  }

  getCoords() {
    let point = this.point;
    let coords = point.coords;
    let coords_msk = point.coords_msk;

    return IS_MSK ? wrapCoords(coords_msk) : wrapCoords(coords);
  }

  onClick() {
    /** onClick **/
  }

  renderImage() {}

  render(options = {}) {
    if (this.coords[0] === null || this.coords[1] === null ) return;

    let image = this.getImage(options);
    let radius = image.width / 2;
    let canvas = this._reactMap.canvas;
    let ctx = canvas.getContext('2d');
    let drawCoords = this.map.projectToPixel(this.coords);

    if (typeof ctx === 'undefined') {
      return;
    }

    if (this.animation) {
      this.animation.update(time);
    }

    ctx.drawImage(image, drawCoords.x - radius, drawCoords.y - radius, radius * 2, radius * 2);
  }

  /**
   * проверка на вхождение координаты в маркер
   */
  contains = (coordinates) => {

    let projectedPixel = this.map.projectToPixel(coordinates);
    let pixelCoords = this.map.projectToPixel(this.coords)

    let radius = this.radius;

    var dx = pixelCoords.x - projectedPixel.x;
    var dy = pixelCoords.y - projectedPixel.y;

    return dx * dx + dy * dy < radius * radius;
  }

}

import { getStatusById } from 'constants/statuses';
import CoordsAnimation from './CoordsAnimation.js';
import { getSmallIcon, getBigIcon } from '../../../../icons/car.js';
import Marker from '../BaseMarker.js';
import Track from '../../Track.js';
import { swapCoords, wrapCoords } from 'utils/geo';
import { getTypeById } from 'utils/labelFunctions';

const DEVICE_PIXEL_RATIO = window.devicePixelRatio;

import {
  SMALL_ICON_RADIUS,
  LARGE_ICON_RADIUS
} from 'constants/CarIcons.js';

// @todo убрать прозрачность на больших зум левелах
const ZOOM_LARGE_ICONS = 8;
const PI_TIMES_TWO = Math.PI * 2;

function normalizeAngle(angle) {

  while (angle < 0) {
    angle += PI_TIMES_TWO
  }
  while (angle > PI_TIMES_TWO) {
    angle -= PI_TIMES_TWO
  }

  return angle;
}

export default class CarMarker extends Marker {

  constructor(point, map) {
    super(point, map);
    point.marker = this;
    this.coords = wrapCoords(swapCoords(point.coords_msk))
    this.track = null;
  }

  isVisible() {
    return this.store.isPointVisible(this.point);
  }

  getImage(options) {
    let map = this.map;
    let zoom = map.getView().getZoom();

    return zoom < ZOOM_LARGE_ICONS && !options.selected ? this.renderSmall(options) : this.renderLarge(options);
  }

  getZoomRatio() {
    let map = this.map;
    let zoom = map.getView().getZoom();
    let coef = 8 - (ZOOM_LARGE_ICONS - zoom);
    return coef > 0 ? coef * .4 : 1 ;
  }

  renderSmall = (options) => {
    let point = this.point;
    let zoomRatio = this.getZoomRatio();
    let radius = this.radius = SMALL_ICON_RADIUS * zoomRatio;

    let image = getSmallIcon(point.status, zoomRatio);


    if (options.showPlates) {
      const title = point.car.gov_number;
      let context = this._reactMap.canvas.getContext('2d');
      let drawCoords = this.map.projectToPixel(this.coords);

      context.fillStyle = 'white';

      var width = context.measureText(title).width;
      var padding = 1 * zoomRatio;

      // dont try to understand
      var rectWidth = width + padding + radius - 3;
      var rectHeight = radius + 4;
      var rectOffsetY = drawCoords.y - radius / 2 - 2;

      context.fillRect(drawCoords.x - rectWidth, rectOffsetY, rectWidth, rectHeight);
      context.fillStyle = 'black';
      context.textBaseline = 'middle';
      context.fillText(title, drawCoords.x - rectWidth + padding, drawCoords.y);
    }

    return image;
  }

  getCar() {
    return this.point.car;
  }

  onClick() {
    this.createTrack();
  }

  createTrack() {
    this.track = new Track(this);
  }

  hasTrackLoaded() {
    return this.track !== null && this.track.isLoaded();
  }

  /**
   * todo showPlates via map options
   * @return {[type]} [description]
   */
  renderLarge = (options = {}) => {

    let point = this.point;
    let color = getStatusById(point.status).color;
    let direction = point.direction;
    let type = getTypeById(point.car ? point.car.type_id : 5);
    let icon = type && type.icon;
    let radius = this.radius = (LARGE_ICON_RADIUS + 6);

    let angle = Math.PI * direction / 180;
    let tipAngle = normalizeAngle(angle - Math.PI / 2);
    let drawCoords = this.map.projectToPixel(this.coords);
    let context = this._reactMap.canvas.getContext('2d');

    const title = point.car.gov_number;

    if (options.showPlates && title) {

      context.fillStyle = 'white';

      var text = title;
      var width = context.measureText(text).width ;
      var padding = 3;

      var rectWidth = width + 2 * padding + radius + 7;
      var rectHeight = 2 * radius - 9;
      var rectOffsetY = drawCoords.y - radius + 5;

      rectWidth = rectWidth * DEVICE_PIXEL_RATIO;

      context.font = 12 * DEVICE_PIXEL_RATIO + 'px \'Helvetica Neue\'';

      if (tipAngle >= 0.5 * Math.PI && tipAngle <= 1.5 * Math.PI) {
        context.fillRect(drawCoords.x, rectOffsetY, rectWidth, rectHeight);
        context.fillStyle = 'black';
        context.textBaseline = 'middle';
        context.fillText(text, drawCoords.x + padding + radius, drawCoords.y);
      } else {
        context.fillRect(drawCoords.x - rectWidth, rectOffsetY, rectWidth, rectHeight);
        context.fillStyle = 'black';
        context.textBaseline = 'middle';
        context.fillText(text, drawCoords.x - rectWidth + padding, drawCoords.y);
      }
    }


    context.fillStyle = color;
    context.beginPath();
    context.save();
    context.translate(drawCoords.x, drawCoords.y);
    context.rotate(tipAngle);
    context.arc(0, 0, radius, Math.PI / 4, -Math.PI / 4);
    context.lineTo(Math.sqrt(2) * radius, 0);
    context.restore();
    context.closePath();
    context.fill();

    if (options.selected) {
      context.strokeStyle = 'white';
      context.lineWidth = 3;
      context.stroke();
    }

    return getBigIcon(icon);
  }

  setPoint(point) {
    this.point = point;
    this.coords = swapCoords(point.coords_msk);

    let [x, y] = this.coords;
    let store = this.store;

    // обновляем трэк, если у точки он загружен
    if (this.hasTrackLoaded()) {
      if (point.timestamp > this.track.getLastPoint().timestamp) {
        this.track.addPoint({
          coords: swapCoords(point.coords),
          coords_msk: swapCoords(point.coords_msk),
          direction: point.direction,
          speed_avg: point.speed,
          distance: point.distance || 'Н/Д',
          speed_max: point.speed_max || 'Н/Д',
          nsat: point.nsat || 'Н/Д',
          timestamp: point.timestamp
        })
      }
    }

    if (!store.state.isRenderPaused) {
      this._animation = new CoordsAnimation(this, x, y, 500);
    } else {
      this.x = x;
      this.y = y;
    }
  }

}

import SimpleAnimation from './SimpleAnimation.js';

export default class CoordsAnimation {

  constructor(target, x, y, interval) {
    this.ended = false;
    this._x = new SimpleAnimation(target, '_projectedX', x, interval);
    this._y = new SimpleAnimation(target, '_projectedY', y, interval);
  }

  update(newTime) {

    if (this.ended) {
      return;
    }

    this._x.update(newTime);
    this._y.update(newTime);

    if (this._x.ended) {
      this.ended = true;
    }

  }
}

export default class SimpleAnimation {

  /**
   * TODO отключить анимации при window.hover
   */

  constructor(target, propertyName, toValue, interval) {
    this._target = target;
    this._propertyName = propertyName;
    this._fromValue = target[propertyName];
    this._currentValue = this._fromValue;
    this._toValue = toValue;
    this._progress = 0;
    this._interval = interval;
    this._speed = 1 / interval;
    this.ended = false;

    this._startTime = -1;
    this._currentTime = -1;
    this._valueSpan = this._toValue - this._fromValue;
  }

  update(newTime) {

    if (this.ended) {
      return;
    }

    if (this._currentTime < 0) {
      this._currentTime = newTime;
      this._startTime = newTime;
      return;
    }

    const oldTime = this._currentTime;
    this._currentTime = newTime;

    const speed = this._speed;
    const oldProgress = this._progress;
    const deltaTime = newTime - oldTime;
    let deltaProgress = speed * deltaTime;
    let newProgress = oldProgress + deltaProgress;

    if (newProgress >= 1.0) {
      newProgress = 1.0;
      this.ended = true;
    }

    this._progress = newProgress;
    deltaProgress = newProgress - oldProgress;

    this._currentValue += this._valueSpan * deltaProgress;
    this._target[this._propertyName] = this._currentValue;
  }
}

import React, { Component } from 'react';
import ClickOutHandler from 'react-onclickout';

export default class ToolbarControl extends Component {

  constructor(props) {
    super(props);
    this.state = {visible: false};
  }

  render(){
    let controlClass = this.state.visible ? 'toolbar-control toggled' : 'toolbar-control'
    let position = {
      top: this.props.top
    };
    let btnClass = 'app-toolbar-btn '+this.props.controlType+' '+this.props.btnClass

    let style = Object.assign({}, {
      display: this.state.visible ? 'block' : 'none',
      position:'relative',
      left:42,
      top:-42
    }, this.props.style);

    return (
      <ClickOutHandler onClickOut={this.handleClickOutside.bind(this)}>
        <div className={controlClass} style={{top: position.top}}>
          <button className={btnClass} onClick={this.toggleVisibility.bind(this)}></button>
          <div className="app-toolbar-fill" style={style}>
            <span className="toolbar-filters-wrap__close-btn" onClick={this.toggleVisibility.bind(this)}>×</span>
            {this.props.children}
          </div>
        </div>
      </ClickOutHandler>
    )
  }

  handleClickOutside(){
    if (this.state.visible === true) {
      this.setState({visible: false});
    }
  }

  toggleVisibility(){
    this.setState({visible: !this.state.visible});

    if ( this.props.onToggle !== undefined ){
      this.props.onToggle();
    }
  }
}

