import { SMALL_ICON_RADIUS } from 'constants/CarIcons.js';
import { getStatusById } from 'constants/statuses';

const PI_TIMES_TWO = Math.PI * 2;
const IS_RETINA = window.devicePixelRatio >= 2;

let CACHE = {};

export default function getSmallIcon(statusId, zoom = 1) {

  let cached = true;

  if (typeof CACHE[statusId] === 'undefined') {
    CACHE[statusId] = [];
    cached = false
  }

  if (typeof CACHE[statusId][zoom] === 'undefined') {
    cached = false
  }

  if (!cached) {
    let radius = SMALL_ICON_RADIUS * zoom * (IS_RETINA ? 2 : 1);
    let color = getStatusById(statusId).color;
    let canvas = document.createElement('canvas');
    canvas.width = canvas.height = 2 * radius;
    let ctx = canvas.getContext('2d');
    ctx.fillStyle = color;
    ctx.strokeStyle = 'white';
    ctx.lineWidth = zoom < 2 ? 1 : 2;

    ctx.beginPath();
    ctx.arc(radius, radius, radius - 2 * zoom, 0, PI_TIMES_TWO);
    ctx.closePath();
    ctx.fill();
    ctx.stroke();

    CACHE[statusId][zoom] = canvas;
    return canvas;
  } else {
    return CACHE[statusId][zoom]
  }
}

import { TRACK_COLORS, TRACK_POINT_RADIUS, TRACK_POINT_BORDER_WIDTH } from 'constants/track.js';

const PI_TIMES_TWO = Math.PI * 2;

let TRACK_POINTS = {};
for (let color in TRACK_COLORS) {

  let canvas = document.createElement('canvas');
  canvas.width = canvas.height = (TRACK_POINT_RADIUS + 1) * 2;

  let ctx = canvas.getContext('2d');

  ctx.strokeStyle = TRACK_COLORS.point_border;
  ctx.fillStyle = TRACK_COLORS[color];
  ctx.lineWidth = TRACK_POINT_BORDER_WIDTH;
  ctx.beginPath();
  ctx.arc(TRACK_POINT_RADIUS + 1, TRACK_POINT_RADIUS + 1, TRACK_POINT_RADIUS, 0, PI_TIMES_TWO, false);

  ctx.fill();
  ctx.stroke();
  TRACK_POINTS[TRACK_COLORS[color]] = canvas;
}

export function getTrackPointByColor(color) {
  return TRACK_POINTS[color];
}

