      customersList: [],
      typesList: [],
      modelsList: [],
      specialModelsList: [],
      ownersList: [],
      fuelTypes: [],
      technicalOperationsList: [],
      workKindsList: [],
      faxogrammsList: [],
      technicalOperationsObjectsList: [],
      technicalOperationsTypesList: [],
      companyStructureList: [],
      positionsList: [],
      organizations: [],

      carsIndex: {},
      modelsIndex: {},
      typesIndex: {},
      ownersIndex: {},
